mapper.select:
SELECT kso2o.*,gp.tel,gp.clientServiceTel,gp.clientServicePhone FROM ( SELECT kso.id,ks.tGoodInfoId,ks.tGoodSkuInfoId,ks.goodBigPic,ks.goodName,ks.marketPrice, ks.supplierName,ks.supplierId,kso.salePrice AS 'saleprice_o2o',ks.tempStoreGoodsFlag, ks.skuFrontDisNameStr, kso.soldStore,ks.distributionFlag,ks.goodIntroduce,ks.isForeigngoods, ks.isExpressToHome,ks.gooddisc,ks.skuIdStr,kso.store,ks.isPayAfterArrival FROM tgoods_publish_kdb_sku ks,tgoods_publish_kdb_sku_o2o kso WHERE ks.tGoodSkuInfoId IN(${skuIds}) AND kso.skuStatus=#{skuStatus}
AND kso.id IN(${o2oIds})
AND ks.tGoodSkuInfoId=kso.skuId) kso2o LEFT JOIN tgoodsprovider gp ON kso2o.supplierId=gp.tGoodsProviderId
